Ext.define('Portal.view.EGRP.mainViewController', {
   extend: 'Ext.app.ViewController',
   alias: 'controller.egrp.main',

   onItemClick: function(dataview, record, item, index, e, eOpts) {
      console.log("ClassName="+record.data.className+" ** index="+index+" ** id="+record.data.id+" ** text="+record.get('name'));
      var P=this.getReferences().MainPanel,
         T=P.items.findBy(
         function (t){
            return t.title===record.get('name');
      }
      );
      if(!T){
         T=P.add({
            xtype:record.data.className,
            closable:true,
            scrollable:'both',

            //iconCls: record.get('iconCls'),
            title:record.get('name'),
            //id: record.data.className+'_'+record.data.id
         });
      }
      //P.setTitle(record.get('name'));
      P.setActiveTab(T);
   },

   onToolClick: function(tool, e, owner, eOpts) {
      var o=this.getReferences().MainPanel.getActiveTab(),
         w=Ext.create('Ext.window.Window', {
            layout:'fit',
            border:0,
            modal:true,
            maximizable:true,
            items:{xtype:o.getXType()},
            title:o.getTitle()
         });
      w.down().setTitle('');
      w.show();
   },

   onMainAfterRender: function(component, eOpts) {
      this.getStore('menu').load();
   }

});
