Ext.define('Portal.view.PhonesBook.PhonesBookViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.PhonesBook',

    ShowUsers: function(filter) {
        var r=this.getReferences();
        Ext.getStore('PhonesBook.UsersStore').load({params:{
            org_id:r.OrgsList.getView().getSelectionModel().getSelection()[0].get('id'),
            missing:r.MissingUsers.checked,
            sayAll:r.showAllUsers.checked,
            filter:filter
        }});
    },

    onPanelShow: function(component, eOpts) {
        Ext.getStore('PhonesBook.Orgs').load();
    },

    onPanelAfterRender: function(component, eOpts) {
        /***** Устанавливаем права на приложение *****/
        var AccessEdit = Portal.util.Util.appAccessEdit(this.getView().xtype),
            ref=this.getReferences();
        //console.log('AccessEdit='+AccessEdit);
        ref.tbOrgEdit.setHidden( !AccessEdit );
        ref.tbUserEdit.setHidden( !AccessEdit );
        Ext.getStore('PhonesBook.Orgs').load();
        Ext.getStore('PhonesBook.UsersStore').load();
    }

});
