Ext.define('Portal.view.benzine.City_EditViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.benzinecity_edit',

    onBtnCityEditSaveClick: function(button, e, eOpts) {
        var me = this,
            form = me.lookupReference('formCityEdit'),
            values = form.getValues(),
            store = Ext.getStore('benzine.city');

        if (form.isValid()) {
            me.getView().mask('Подождите...');

            Ext.Ajax.request({
                url: 'data/benzine/city-edit.php',
                params: values,
                success: function(r) {
                    store.load();
                }
            });
            me.getView().unmask();
            me.getView().close();
        }
    },

    onBtnCityEditCancelClick: function(button, e, eOpts) {
        this.getView().close();
    }

});
