Ext.define('Portal.view.benzine.Tour_EditViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.benzinetour_edit',

    onFldBenzineTourTourFromChange: function(field, newValue, oldValue, eOpts) {
        var form=this.lookupReference('formTourEdit'),
            values=form.getValues();
        Ext.Ajax.request({
            url: 'data/benzine/tour-read-distance.php',
            params: values,
            success: function(response){
                var d = Portal.util.Util.decodeJSON(response.responseText);
                form.getForm().setValues({
                    distance:d.data.distance
                });
            }
        });
    },

    onFldBenzineTourTourToChange: function(field, newValue, oldValue, eOpts) {
        var form=this.lookupReference('formTourEdit'),
            values=form.getValues();
        Ext.Ajax.request({
            url: 'data/benzine/tour-read-distance.php',
            params: values,
            success: function(response){
                var d = Portal.util.Util.decodeJSON(response.responseText);
                form.getForm().setValues({
                    distance:d.data.distance
                });
            }
        });
    },

    onBtnBenzineTourEditSaveClick: function(button, e, eOpts) {
        var me = this,
            form = me.lookupReference('formTourEdit'),
            record = form.getRecord(),
            values = form.getValues(),
            store = Ext.getStore('benzine.tour');

        if (form.isValid()) {
            me.getView().mask('Подождите...');

            Ext.Ajax.request({
                url: 'data/benzine/tour-edit.php',
                params: values,
                success: function(r) {
                    store.load({
                        params:{
                            tour_id:values.tour_id
                        }
                    });
                },
                failure: function(r) {
                    Ext.Msg.alert('Failed', r.result.msg);
                }
            });

            me.getView().unmask();
            me.getView().close();
        }
    },

    onBtnBenzineTourEditCancelClick: function(button, e, eOpts) {
        this.getView().close();
    },

    onBtnBenzineTourEditAddressAddClick: function(button, e, eOpts) {
        var edit = Ext.create('Portal.view.benzine.Address_Edit').show();
        edit.getReferences().formAddressEdit.getForm().setValues({
            new: 1
        });
    }

});
