Ext.define('Portal.view.sys.PortalFAQEditViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.sysportalfaqedit',

    onBtnSysPortalFAQEditSaveClick: function(button, e, eOpts) {
        var me = this,
            form = me.getReferences().form,
            values = form.getValues(),
            store = Ext.getStore('sys.PortalFAQ');

        if (form.isValid()) {
            me.getView().mask('Подождите...');
            Ext.Ajax.request({
                url: 'data/sys/portalFAQ-edit.php',
                params: values,
                success: function(r) {
                    store.load();
                }
            });
            me.getView().unmask();
            me.getView().close();
        }
    },

    onBtnSysPortalFAQCancelClick: function(button, e, eOpts) {
        this.getView().close();
    },

    onBtnPortalFAQEditFileAddClick: function(button, e, eOpts) {
        var Input=Ext.create('Portal.view.sys.InputFile').show(),
            refs = Input.getReferences(),
            rI=refs.form.getForm();

        //refs.formCityEdit.getForm().loadRecord(record);
        //refs.formCityEdit.getForm().setValues(record.getData());
        rI.setConfig({url:'data/sys/portaFAQ-InputFile.php'});
        rI.setValues({v:0, r:0});
    }

});
