Ext.define('Portal.view.sys.setOrgs.OrgEditViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.syssetorgsorgedit',

    onButtonClick: function(button, e, eOpts) {
        var me = this.getView(),
            form = me.down('form'),
            values = form.getValues(),
            store = Ext.getStore('sys.setOrgs.Orgs');

        if (form.isValid()) {
            me.mask('Подождите...');

            Ext.Ajax.request({
                url: 'data/sys/setOrgs/orgs-edit.php',
                params: values,
                success: function(r) {
                    console.log(r);
                    store.reload();
                    me.unmask();
                    me.close();
                }
            });
        }
    }

});
